import React,{useState} from 'react';
import './App.css';
import GithubImage from './github.png'

function App() {
const [search,setSearch] = useState('');

/*
Função para tirar de forma padrao e evento de enviar o formulario
*/
const handleSubmit= (event) =>{
  // esse evento tira o reflesh da pagina 
  event.preventDefault();
  // ele acessar a api
  fetch(`https://api.github.com/users/${search}`)
  .then(response=> response.json())
  .then(useResponse)
}
const handleChange=(event) =>{
  /*aqui voce ira mostrar o que esta escrevendo no input
  console.log(event.target.value);*/
  //para aparecer no input vc tem que sobreEscrever
  setSearch(event.target.value);
}

  return (

    <div className="container text-center">
      <h1 className="py-5 text-uppercase">Github profile</h1>

      <form onSubmit={handleSubmit}>
        <div className="form-group">

          <div className="input-group">
            <input
              type="text"
              className="form-control"
              required
value={search}
onChange={handleChange}
            />
            <span className="input-group-btn">
              <button type="submit" className="btn btn-success">
                Search
              </button>
            </span>
          </div>

        </div>

      </form>
      <div className="py-5">

        <img src={GithubImage}
          alt=""
          className="responsive rounded-circle"
          height="200px" />
        <h1 className="pt-5">
          <a href="https://github.com/GilmarAlves99"
            target="_new">
            Gilmar Alves
          </a>
        </h1>sdsd
        <h3>Pariquera-Açu</h3>
        <p>
         <a href="https://www.linkedin.com/in/gilmar-zezilia-alves-336610182/" target="_new" className="text-info">https://www.linkedin.com/in/gilmar-zezilia-alves-336610182/</a>
        </p>
      </div>
    </div>
  );
}

export default App;
